div.script:
    function search_data(v){
    var c = $igk(document.body).qselect('#listview .r').first();
    c.setHtml(external.search_for(v)).init();
    c.select('input').each_all(function(){
    android_init_event.apply(this);
    });
    };


    function android_update_value(id,v){
    var s = external.update_value(id, v);
    var g = $igk(this).select('^tr').first().o;
    var m = igk.dom.loadDocument(s);

    g.parentNode.replaceChild(m ,g);
    $igk(m).select("input").each_all(function(){
    android_init_event.apply(this);
    });

    }
    //register event input
    function android_init_event(){

    var q = this;
    q.reg_event("change", function(evt){
    android_update_value.apply(this, [this.id, this.value]);
    }).reg_event("keypress", function(evt){
    if (evt.keyCode ==13){
    var e=document.createEvent('HTMLEvents');
    e.initEvent("change",true,true);
    q.dispatchEvent(e);

    }
    });

    };

    function android_edit_info(t){
      var g = $igk(t).select("^.igk-roll-in").first();
      var p = g.o.parentNode;    
      g.remove();
      var s = external.EditProperties();
      $igk(p).setHtml("
"+s+"").init();
      p.appendChild(g.o);


    }
    (function(){
    igk.ready(function(){
    var q = $igk("#search").first();
    q.reg_event("keypress", function(e){
    if (e.keyCode ==13){
    search_data(q.o.value);
    }
    });

    });

    })();

(function(q){			
igk.ready(function(){
	//for all input update the values
	$igk(q).select('input').each_all(function(){
		android_init_event.apply(this);
	});
});
})(igk.getParentScript());
